use Memoize;
memoize('energy_converter');

################################################################################
#   change 00000000000000000000000000000000000000000000010f0cf064dd59200000 to 5000000000000000000000 then to 5000
sub energy_converter {
 my $in= shift @_;
 my $out=hex($in);

 # if less than 18 chars, pad with zeroes
 if (length($out) < 18){
   $out = "0" x (18-length($out)) . $out;
 }

 $out =~ s/(\d{18})$/.$1/;

 $out = sprintf "%30.18f", $out;

 return $out;
}


