sub string_range_smush {
  my $string = shift @_;
  $string =~ s/\s*,\s*/ /g;
  $string =~ s/\b0+([^0])/$1/g;
  my @numbers = ( $string =~ m/\b(\d+)/g);
  return range_smush( \@numbers );
}


sub range_smush {
  my $aref=shift @_;

  # make a sorted list of unique values
  my %seen = ();                             
  my @range = sort {$a <=> $b} grep { ! $seen{$_} ++ } @$aref;

  my $return_string='';
  my $ranging=0;

  return $return_string unless scalar(@range)>0;

  my $prev= shift @range;
  $return_string .= $prev;

  my $i;
  foreach $i (@range){
    if ($prev + 1 == $i){
      $ranging=1;
    }else{
      if ($ranging){
        $return_string .= "-$prev,$i";
        $ranging=0;
      }else{
        $return_string .= ",$i";
      }
    }
    $prev=$i;
  }

  if ($ranging){
    $return_string .= "-$prev";
  }

 return $return_string;
}



